import React from 'react';
import { useState, useEffect } from 'react';
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import styled from "styled-components";

const CenteredContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 200vh;
  background-color: #282c34;
`;

const Heading = styled.h1`
  color: black;
  font-size: 1.5rem;
  text-align: center;
`;
const DashboardContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  background-color: #f4f4f4;
  min-height: 200vh;
  background-image: url('/assets/bg.jpg');
  background-size: cover;
  background-position:center;
`;

const Title = styled.h1`
  color: #333;
  font-size: 32px;
  margin-top:80px;
  margin-bottom: 10px;
`;
const ServiceList=styled.div`
  width: 80%;
  display:flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background: white;
  border-radius: 8px;
  padding: 10px;
  margin-bottom: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;
const Services = styled.div`
  width: 80%;
  justify-content: center;
  align-items: center;
 

`;
const ServiceTitle=styled.h2`
`;

const ServiceButton = styled.button`
  background-color: #007bff;
  color: white;
  border: none;
  border-radius: 0px;
  padding: 10px 15px;
  margin: 5px;
  cursor: pointer;
  width:182px;
  height:60px;
  &:hover {
    background-color: #0056b3;
  }
`;

const CustomizationContainer = styled.div`
  width: 80%;
  background: white;
  border-radius: 8px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const FieldInput = styled.input`
  padding: 10px;
  margin: 10px 0;
  width: 100%;
  border-radius: 4px;
  border: 1px solid #ccc;
`;

const AddFieldButton = styled.button`
  background-color: #28a745;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 10px 15px;
  cursor: pointer;
  &:hover {
    background-color: #218838;
  }
`;

const SaveButton = styled.button`
  background-color: #17a2b8;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 10px 15px;
  cursor: pointer;
  margin-top: 20px;
  &:hover {
    background-color: #138496;
  }
`;
const CardContainer = styled.div`
  width: 80%;
  background: #fff;
  border-radius: 8px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  margin: 20px 0;
`;

const FixedField = styled.p`
  font-weight: bold;
  margin: 5px 0;
`;

const CustomField = styled.p`
  margin: 5px 0;
`;

function Home() {
    const location = useLocation();
    const navigate = useNavigate();
    const [user, setUser] = useState(location.state?.user);
    const [loading, setLoading] = useState(!user);
    const [services, setServices] = useState([
        { id: 1, name: 'Income Tax Certificate', formFields: [] },
        { id: 2, name: 'Caste Certificate', formFields: [] },
        { id: 3, name: 'Land Certificate', formFields: [] },
        {id:4, name:'Death Certificate', formFields:[]},
      ]);
      const [selectedService, setSelectedService] = useState(null);
      const [savedForms, setSavedForms] = useState([]);
      const fixedFields = [
        'Applicant Name', 'Mobile No.', 'Age', 'Gender', 'DOB', 'Aadhaar No.', 'Email'
    ];
      const [newField, setNewField] = useState('');
    useEffect(() => {
        if (!user) {
            axios.get('http://localhost:3001/user', { withCredentials: true })
                .then(response => {
                    if (response.data.user) {
                        setUser(response.data.user);
                    } else {
                        navigate("/login");
                    }
                })
                .catch(() => navigate("/login"))
                .finally(() => setLoading(false));
        } else {
            setLoading(false);
        }
    }, [user, navigate]);
    const addFieldToForm = () => {
        if (newField && selectedService) {
          setServices((prevServices) =>
            prevServices.map((service) =>
              service.id === selectedService.id
                ? { ...service, formFields: [...service.formFields, newField] }
                : service
            )
          );
          setNewField('');
        }
      };
   
      const saveForm = () => {
        if (selectedService) {
            const form = {
                serviceName: selectedService.name,
                formFields: [...fixedFields, ...selectedService.formFields],
            };
            setSavedForms([...savedForms, form]);
            alert('Customizations saved successfully.');
        }
    };
      const renderServices = () => {
        return services.map((service) => (
          <ServiceButton key={service.id} onClick={() => setSelectedService(service)}>
            {service.name}
          </ServiceButton>
        ));
      };
    
      const renderSelectedService = () => {
        if (!selectedService) return <p>Please select a service to customize.</p>;
        return (
            <CustomizationContainer>
              <h2>Customizing: {selectedService.name} Application form</h2>
              <h3>Form Fields:</h3>
              <ul>
                    {fixedFields.map((field, index) => (
                        <li key={index}>{field}</li>
                    ))}
                </ul>
              <ul>
                {selectedService.formFields.map((field, index) => (
                  <li key={index}>{field}</li>
                ))}
              </ul>
              <FieldInput
                type="text"
                value={newField}
                onChange={(e) => setNewField(e.target.value)}
                placeholder="Enter a new field"
              />
              <AddFieldButton onClick={addFieldToForm}>Add Field</AddFieldButton>
      
      
              <SaveButton onClick={() => alert('Customizations saved successfully.')}>
                Save Customizations
              </SaveButton>
            </CustomizationContainer>
          );
        };
        const renderSavedForms = () => {
            return savedForms.map((form, index) => (
                <CardContainer key={index}>
                    <h3>{form.serviceName} Application Form</h3>
                    {form.formFields.map((field, i) => (
                        <FixedField key={i}>{field}</FixedField>
                    ))}
                </CardContainer>
            ));
        };
      
    if (loading) {
        return (
            <CenteredContainer>
                <Heading>Loading...</Heading>
            </CenteredContainer>
        );
    }

    return (
       
           
         <DashboardContainer>
         <Title>Officer Dashboard</Title>
         <Heading>Welcome Home {user && user.name} !</Heading>
         <ServiceList>
            <ServiceTitle>Services</ServiceTitle>
           <Services>
           {renderServices()}
           </Services>         
         </ServiceList>
         {renderSelectedService()}
         <h2>Saved Application Forms</h2>
            {renderSavedForms()}
       </DashboardContainer>
    );
}

export default Home;
