import React from 'react';
import { useState, useEffect } from 'react';
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import styled from "styled-components";

const CenteredContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 200vh;
  background-color: #282c34;
`;

const Heading = styled.h1`
  color: black;
  font-size: 1.5rem;
  text-align: center;
`;
const DashboardContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  background-color: #f4f4f4;
  min-height: 200vh;
  background-image: url('/assets/bg.jpg');
  background-size: cover;
  background-position:center;
`;

const Title = styled.h1`
  color: #333;
  font-size: 32px;
  margin-top:80px;
  margin-bottom: 10px;
`;
const ServiceList=styled.div`
  width: 80%;
  display:flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  background: white;
  border-radius: 8px;
  padding: 10px;
  margin-bottom: 10px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;
const Services = styled.div`
  width: 80%;
  justify-content: center;
  display:flex;
  align-items: center;
`;
const ServiceTitle=styled.h2`
`;

const ServiceButton = styled.button`
  background-color: #007bff;
  color: white;
  justify-content: center;
  align-items: center;
  border: none;
  border-radius: 0px;
  padding: 10px 15px;
  margin: 5px;
  cursor: pointer;
  width:182px;
  height:60px;
  &:hover {
    background-color: #0056b3;
  }
`;

const CustomizationContainer = styled.div`
  width: 80%;
  background: white;
  border-radius: 8px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const FieldInput = styled.input`
  padding: 10px;
  margin: 10px 0;
  width: 100%;
  border-radius: 4px;
  border: 1px solid #ccc;
`;

const AddFieldButton = styled.button`
  background-color: #28a745;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 10px 15px;
  cursor: pointer;
  &:hover {
    background-color: #218838;
  }
`;

const SaveButton = styled.button`
  background-color: #17a2b8;
  color: white;
  border: none;
  border-radius: 4px;
  padding: 10px 15px;
  cursor: pointer;
  margin-top: 20px;
  &:hover {
    background-color: #138496;
  }
`;
const CardContainer = styled.div`
  width: 80%;
  background: #fff;
  border-radius: 8px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  margin: 20px 0;
`;

const FixedField = styled.p`
  font-weight: bold;
  margin: 5px 0;
`;
const FormCard = styled.div`
    background-color: white;
    border-radius: 8px;
    padding: 20px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    margin-top: 20px;
    width: 80%;
`;


const fixedFields = [
  { label: 'Applicant Name', type: 'text', name: 'name', required: true },
  { label: 'Mobile No.', type: 'tel', name: 'mobile', required: true, pattern: "[0-9]{10}" },
  { label: 'Age', type: 'number', name: 'age', required: true, min: 1 },
  { label: 'Gender', type: 'select', name: 'gender', options: ['Male', 'Female', 'Other'], required: true },
  { label: 'Date of Birth', type: 'date', name: 'dob', required: true },
  { label: 'Aadhaar No.', type: 'text', name: 'aadhaar', required: true },
  { label: 'Email', type: 'email', name: 'email', required: true },
];

function Home() {
  const location = useLocation();
  const navigate = useNavigate();
  const [user, setUser] = useState(location.state?.user);
  const [loading, setLoading] = useState(!user);
  const [services, setServices] = useState([
      { id: 1, name: 'Income Tax Certificate', formFields: [] },
      { id: 2, name: 'Caste Certificate', formFields: [] },
      { id: 3, name: 'Land Certificate', formFields: [] },
      { id: 4, name: 'Death Certificate', formFields: [] },
  ]);
  const [selectedService, setSelectedService] = useState(null);
  const [newField, setNewField] = useState('');
  const [customFields, setCustomFields] = useState([]);
  const [formData, setFormData] = useState({});
  
  // Fetch user data
  useEffect(() => {
      if (!user) {
          axios.get('http://localhost:3001/user', { withCredentials: true })
              .then(response => {
                  if (response.data.user) {
                      setUser(response.data.user);
                  } else {
                      navigate("/login");
                  }
              })
              .catch(() => navigate("/login"))
              .finally(() => setLoading(false));
      } else {
          setLoading(false);
      }
  }, [user, navigate]);

  const addFieldToForm = () => {
      if (newField) {
          setCustomFields([...customFields, { label: newField, type: 'text', name: newField.toLowerCase().replace(/\s/g, '_') }]);
          setNewField('');
      }
  };

  const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
      alert('Form Submitted Successfully!');
  };

  const renderFields = () => (
      <>
          {fixedFields.map((field, index) => (
              <div key={index}>
                  <label>{field.label}</label>
                  {field.type === 'select' ? (
                      <select name={field.name} onChange={handleChange} required={field.required}>
                          {field.options.map(option => <option key={option} value={option}>{option}</option>)}
                      </select>
                  ) : (
                      <input
                          type={field.type}
                          name={field.name}
                          onChange={handleChange}
                          required={field.required}
                          pattern={field.pattern || null}
                          min={field.min || null}
                      />
                  )}
              </div>
          ))}
          {customFields.map((field, index) => (
              <div key={index}>
                  <label>{field.label}</label>
                  <input type={field.type} name={field.name} onChange={handleChange} />
              </div>
          ))}
      </>
  );

  const renderSelectedService = () => {
      if (!selectedService) return <p>Please select a service to customize.</p>;
      return (
          <CustomizationContainer>
              <h2>Customizing: {selectedService.name} Application Form</h2>
              <FieldInput
                  type="text"
                  value={newField}
                  onChange={(e) => setNewField(e.target.value)}
                  placeholder="Enter a new field"
              />
              <AddFieldButton onClick={addFieldToForm}>Add Field</AddFieldButton>
              <FormCard>
                  {renderFields()}
                  <SaveButton onClick={handleSubmit}>Proceed to Apply</SaveButton>
              </FormCard>
          </CustomizationContainer>
      );
  };

  if (loading) {
      return <CenteredContainer><Heading>Loading...</Heading></CenteredContainer>;
  }

  return (
      <DashboardContainer>
          <Title>Officer Dashboard</Title>
          <Heading>Welcome Home {user && user.name}!</Heading>
          <ServiceList>
              <ServiceTitle>Services</ServiceTitle>
              <Services>
              {services.map((service) => (
                  <ServiceButton key={service.id} onClick={() => setSelectedService(service)}>
                      {service.name}
                  </ServiceButton>
             ))}
             </Services>
          </ServiceList>
          {renderSelectedService()}
      </DashboardContainer>
  );
};

export default Home;
