import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import Logout from './Logout';

const HeaderBar = styled.header`
  background-color: #333;
  padding: 1rem 0;
`;

const Toolbar = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: bold;
  color: #fff;
  margin: 0;
`;

const StyledButton = styled(Link)`
  margin-right: 20px;
  font-size: 1.2rem;
  font-weight: 700;
  padding: 0.3rem 1.4rem;
  text-decoration: none;
  color: #fff;
  background-color: ${(props) => (props.variant === 'error' ? '#d32f2f' : '#2e7d32')};
  border: none;
  border-radius: 4px;
  text-align: center;
  display: inline-block;

  &:hover {
    background-color: ${(props) => (props.variant === 'error' ? '#b71c1c' : '#1b5e20')};
  }
`;

function Header({ isLoggedIn, setIsLoggedIn }){
    return (
        <HeaderBar>
            <Toolbar>
                <Title>CERTIfast</Title>
                {!isLoggedIn ? (
                    <>
                        <StyledButton variant="error" to="/login">
                            Login
                        </StyledButton>
                        <StyledButton variant="success" to="/signup">
                            Signup
                        </StyledButton>
                    </>
                ) : (
                    <Logout setIsLoggedIn={setIsLoggedIn} />
                )}
            </Toolbar>
        </HeaderBar>
    );
};
export default Header;
